mapper.select:
SELECT r.RELEASE_INFO_ID, r.TITEL, r.CONTENT, r.ATTACHMENT_ID, r.TYPE, r.BELONG_USER_ID, r.BELONG_CIRCLE_ID, r.STATUS, r.IS_DELETE, r.CREATED_BY, r.CREATED_BY_USER, r.CREATION_DATE, r.LAST_UPDATED_BY_USER, r.LAST_UPDATED_BY, r.LAST_UPDATE_DATE, ( SELECT count( 1 ) FROM wcc_praise_favorite f WHERE 1 = 1 AND f.STATUS = 1 AND f.IS_DELETE = 0 AND f.TYPE = 0 AND f.BELONG_ID = r.RELEASE_INFO_ID ) AS praise_count, ( SELECT count( 1 ) FROM wcc_response_details d WHERE 1 = 1 AND d.STATUS = 1 AND d.IS_DELETE = 0 AND d.BELONG_ID = r.RELEASE_INFO_ID ) AS comment_count, u.NAME, u.NICKNAME, u.WECHAT_HEAD_PORTRAIT_ADDRESS FROM wcc_release_info r LEFT JOIN wcc_user u ON u.USER_ID=r.BELONG_USER_ID WHERE 1 = 1 AND r.STATUS = 1 AND r.IS_DELETE = 0
AND r.RELEASE_INFO_ID IN (#{releaseInfoIdsStr})
AND r.BELONG_USER_ID = #{userId}
AND r.TYPE = #{type}
ORDER BY r.CREATION_DATE DESC LIMIT #{current} ,#{size}
